import React, { Component } from 'react';

class digiteAlgo extends Component {
  render() {

    const { value } =  this.props

    return (
      <fieldset> Escreva algo aqui:
      <div>
        <textarea 
        name='digiteAlgo'
         placeholder='Escreva Aqui!' 
         value={value}
         onChange={ this.handleGenerico }
         />
      </div>
      </fieldset>
    )
  }
}

export default digiteAlgo
