import React, { Component } from 'react';

class Form extends Component {
  constructor() {
    super()
    this.state = {
      algo: '',
    }
    this.handleGenerico = this.handleGenerico.bind(this)
  }

  handleGenerico ({ target }) {
    const { name, value } = target;
    this.setState({
      [name]: value,
    })
  }

  render() {
    return (
      <div>
        <form>
          <label> Escreva algo aqui:
          <div>
            <textarea 
            name='algo'
             placeholder='Escreva Aqui!' 
             onChange={ this.handleGenerico }
             />
          </div>
          </label>

            <label> Escreva seu nome:
          <div>
              <input name='nome' type='text' />
          </div>
            </label>

            <label> Digite sua idade:
          <div>
              <input name='idade' type='number'/>
          </div>
            </label>

          <div>
            <select name='estado' > Escolha seu estado:
              <option>  </option>
              <option>GO</option>
              <option>DF</option>
              <option>PA</option>
            </select>

          </div>


        </form>
      </div>

    )
  }
}


export default Form
